INSERT OR IGNORE INTO campaigns (id, title, slug, description, content, category_id, target_amount, collected_amount, donor_count, start_date, end_date, status, is_featured, created_by) VALUES
('camp-001', 'Wakaf Produktif Al-Ihsan', 'wakaf-produktif-al-ihsan', 'Program wakaf produktif untuk membangun lahan usaha yang hasilnya disalurkan kepada masyarakat kurang mampu di Sukabumi.', 'Program Wakaf Produktif Al-Ihsan bertujuan untuk mengelola aset wakaf secara produktif sehingga menghasilkan manfaat ekonomi berkelanjutan. Dana wakaf akan digunakan untuk membangun lahan usaha pertanian dan peternakan yang hasilnya disalurkan untuk pendidikan, kesehatan, dan kebutuhan pokok masyarakat.', 'cat-c-005', 50000000, 37500000, 124, '2026-01-15', '2026-06-15', 'active', 1, 'admin-001'),
('camp-002', 'Beasiswa Anak Yatim 2026', 'beasiswa-anak-yatim-2026', 'Bantuan biaya pendidikan untuk anak-anak yatim piatu di wilayah Sukabumi agar tetap bisa bersekolah.', 'Program Beasiswa Anak Yatim 2026 menyediakan bantuan biaya pendidikan mulai dari tingkat SD hingga SMA. Mencakup biaya sekolah, seragam, buku, dan alat tulis. Target 50 anak yatim piatu di Sukabumi dan sekitarnya.', 'cat-c-001', 50000000, 22500000, 89, '2026-02-01', '2026-12-31', 'active', 1, 'admin-001'),
('camp-003', 'Bangun Masjid Desa Cikaret', 'bangun-masjid-desa-cikaret', 'Pembangunan masjid di Desa Cikaret yang sudah rusak parah agar masyarakat bisa beribadah dengan nyaman.', 'Masjid di Desa Cikaret sudah berusia lebih dari 30 tahun dan mengalami kerusakan parah. Program ini bertujuan untuk merenovasi dan memperluas masjid agar dapat menampung lebih banyak jamaah.', 'cat-c-003', 100000000, 90000000, 312, '2025-10-01', '2026-04-01', 'active', 1, 'admin-001'),
('camp-004', 'Pemberdayaan UMKM Sukabumi', 'pemberdayaan-umkm-sukabumi', 'Program pelatihan dan pendampingan UMKM di Sukabumi untuk meningkatkan kapasitas usaha dan ekonomi masyarakat.', 'Program pemberdayaan UMKM mencakup pelatihan manajemen bisnis, pemasaran digital, dan akses permodalan. Target 100 pelaku UMKM di Sukabumi dan sekitarnya.', 'cat-c-006', 50000000, 15000000, 56, '2026-02-15', '2026-08-15', 'active', 0, 'admin-001'),
('camp-005', 'Bantuan Bencana Alam Sukabumi', 'bantuan-bencana-alam-sukabumi', 'Penggalangan dana untuk korban bencana alam di wilayah Sukabumi, mencakup kebutuhan pokok dan tempat tinggal.', 'Dana bantuan digunakan untuk pengadaan sembako, selimut, obat-obatan, dan perbaikan rumah warga terdampak bencana.', 'cat-c-003', 200000000, 200000000, 567, '2025-06-01', '2025-12-31', 'completed', 0, 'admin-001'),
('camp-006', 'Renovasi Panti Asuhan Ar-Rahman', 'renovasi-panti-asuhan-ar-rahman', 'Renovasi panti asuhan Ar-Rahman yang sudah tidak layak huni untuk memberikan tempat tinggal yang lebih baik bagi anak-anak.', 'Panti asuhan Ar-Rahman menampung 35 anak yatim piatu. Kondisi bangunan sudah sangat memprihatinkan dan perlu renovasi menyeluruh meliputi atap, lantai, kamar mandi, dan dapur.', 'cat-c-003', 75000000, 0, 0, '2026-04-01', '2026-10-01', 'draft', 0, 'admin-001');

