-- Sample News
INSERT OR IGNORE INTO news (id, title, slug, excerpt, content, category_id, author_id, status, view_count, published_at, created_at) VALUES
('news-001', 'Penyaluran Donasi Wakaf Produktif Tahap Pertama', 'penyaluran-donasi-wakaf-produktif-tahap-pertama', 'Yayasan SBK telah berhasil menyalurkan donasi wakaf produktif tahap pertama kepada masyarakat penerima manfaat.', 'Alhamdulillah, pada tanggal 28 Februari 2026, Yayasan Saling Bantu Kreasi Indonesia telah berhasil menyalurkan donasi wakaf produktif tahap pertama. Dana yang telah terkumpul sebesar Rp 37,5 juta dari target Rp 50 juta telah dialokasikan untuk pembangunan lahan usaha pertanian di kawasan Cikaret, Sukabumi.\n\nPenyaluran ini melibatkan 15 keluarga penerima manfaat yang akan mengelola lahan tersebut secara berkelanjutan. Hasil dari pertanian ini nantinya akan dibagi antara pengelola dan dana sosial yayasan.', 'cat-n-004', 'admin-001', 'published', 234, '2026-02-28 08:00:00', '2026-02-28 08:00:00'),
('news-002', 'Kegiatan Bakti Sosial di Desa Cikaret', 'kegiatan-bakti-sosial-desa-cikaret', 'Bersama relawan, YSB menyelenggarakan bakti sosial meliputi pembagian sembako dan pengobatan gratis.', 'Pada tanggal 25 Februari 2026, Yayasan Saling Bantu Kreasi bersama 30 relawan menyelenggarakan kegiatan bakti sosial di Desa Cikaret. Kegiatan ini meliputi pembagian 200 paket sembako, pengobatan gratis untuk 150 warga, dan kegiatan bersih-bersih lingkungan.\n\nKegiatan ini merupakan rangkaian program rutin yayasan dalam memberdayakan masyarakat di wilayah Sukabumi.', 'cat-n-001', 'admin-001', 'published', 189, '2026-02-25 10:00:00', '2026-02-25 10:00:00'),
('news-003', 'Kisah Inspiratif: Anak Yatim Raih Beasiswa', 'kisah-inspiratif-anak-yatim-raih-beasiswa', 'Muhammad Rizki, salah satu penerima beasiswa dari Yayasan SBK, berhasil meraih prestasi akademik membanggakan.', 'Muhammad Rizki (15), salah satu penerima beasiswa dari program Beasiswa Anak Yatim Yayasan SBK, berhasil meraih peringkat pertama di kelasnya. Rizki yang ditinggal ayahnya sejak kecil, kini tinggal bersama ibunya yang bekerja sebagai buruh cuci.\n\n"Saya sangat bersyukur mendapat bantuan dari Yayasan SBK. Tanpa beasiswa ini, saya mungkin tidak bisa melanjutkan sekolah," ungkap Rizki dengan penuh haru.', 'cat-n-003', 'admin-001', 'published', 567, '2026-02-20 09:00:00', '2026-02-20 09:00:00'),
('news-004', 'Pengumuman: Pembukaan Campaign Ramadhan 2026', 'pengumuman-pembukaan-campaign-ramadhan-2026', 'Yayasan SBK membuka campaign khusus Ramadhan 2026 untuk program santunan dan buka puasa bersama.', 'Menyambut bulan suci Ramadhan 1447 H, Yayasan Saling Bantu Kreasi Indonesia membuka campaign khusus yang mencakup:\n\n1. Program Santunan Anak Yatim Ramadhan\n2. Buka Puasa Bersama 1000 Dhuafa\n3. Paket Sembako Ramadhan\n4. Zakat Fitrah dan Fidyah\n\nMari bersama kita sambut Ramadhan dengan kebaikan.', 'cat-n-002', 'admin-001', 'draft', 0, NULL, '2026-03-01 06:00:00'),
('news-005', 'Laporan Keuangan Tahunan 2025', 'laporan-keuangan-tahunan-2025', 'Laporan transparansi keuangan Yayasan Saling Bantu Kreasi Indonesia untuk tahun 2025.', 'Sebagai bentuk akuntabilitas dan transparansi, berikut laporan keuangan Yayasan Saling Bantu Kreasi Indonesia untuk tahun 2025:\n\nTotal Donasi Masuk: Rp 245.800.000\nTotal Penyaluran: Rp 230.500.000\nBiaya Operasional: Rp 12.300.000\nSaldo Akhir: Rp 3.000.000\n\nLaporan lengkap dapat diunduh melalui link berikut.', 'cat-n-004', 'admin-001', 'published', 1203, '2026-01-15 08:00:00', '2026-01-15 08:00:00'),
('news-006', 'Kerja Sama dengan Pemerintah Daerah Sukabumi', 'kerja-sama-pemerintah-daerah-sukabumi', 'YSB menandatangani MoU dengan Pemda Sukabumi untuk program pemberdayaan masyarakat.', 'Pada 10 Desember 2025, Yayasan Saling Bantu Kreasi Indonesia resmi menandatangani nota kesepahaman (MoU) dengan Pemerintah Daerah Kabupaten Sukabumi. MoU ini mencakup kerja sama dalam program pemberdayaan masyarakat, pelatihan UMKM, dan pengelolaan wakaf produktif.', 'cat-n-001', 'admin-001', 'published', 342, '2025-12-10 10:00:00', '2025-12-10 10:00:00');

